
public class condicionalCalc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int hora = 20;
	
		if(hora < 12) {
			System.out.println("bom dia");
		}
		else if(hora < 18) {
			System.out.println("boa tarde");
		}
		else if(hora > 18) {
			System.out.println("boa noite");
		}
		}
	}


	


